mapper.select:
   select *  from business_apply 
   
     
        state=#{state}
     
   
   order by  addtime
		
			ASC
		
		
			DESC
		
		limit #{start},#{limit}

mapper.update:
update business_apply 

   reason=#{reason},
   state=#{state}

where aid=#{aid}

update business_user
businesscode=#{businesscode}, name=#{name}, contactphone=#{contactphone}, address=#{address}, officestarttime=#{officestarttime}, officeendtime=#{officeendtime}, headurl=#{headurl}, province=#{province}, city=#{city}, des=#{des}, desdetail=#{desdetail}, latitude=#{latitude}, longitude=#{longitude}, doorpics=#{doorpics}, shoppics=#{shoppics}, businessserviceliststr=#{businessserviceliststr},
contactname=#{contactname}, type=#{type}, mainbrand=#{mainbrand}, businessarea=#{businessarea}, staffcount=#{staffcount}, age=#{age}, recommendbrand=#{recommendbrand}, license=#{license}, other=#{other}, isapprove=#{isapprove}, state=#{state}, lbsid=#{lbsid}, busikeyword=#{busikeyword}, businessactivityliststr=#{businessactivityliststr}
where userid=#{userid}

update business_userinfoapply
baseinfostr=#{baseinfostr}, environmentalstr=#{environmentalstr}, businessserviceliststr=#{businessserviceliststr}, businessactivityliststr=#{businessactivityliststr},
where userid=#{userid}

update business_apply
name=#{name}, contactname=#{contactname}, contactphone=#{contactphone}, type=#{type}, mainbrand=#{mainbrand}, address=#{address}, businessarea=#{businessarea}, staffcount=#{staffcount}, age=#{age}, recommendbrand=#{recommendbrand}, license=#{license}, other=#{other}, province=#{province}, city=#{city}, businessserviceliststr=#{businessserviceliststr}, longitude=#{longitude}, latitude=#{latitude},
where aid=#{aid}

mapper.select:
select count(1) from usecarmain u
left join usecarmaingroup ug on ug.groupid=u.groupid where u.groupid=#{groupid}
left join usecarmaingroup ug on ug.groupid=u.groupid where ug.type>=4

select * from `business_service_v_3_8_0` where 1=1
and buid=#{buid}
order by orderindex desc
