mapper.select:
select COUNT(1) from motomall_product

parenttype=#{search_parenttype}
  and type=#{search_type}
  and `level`=#{search_level}
  and newprice >= #{search_price_start}
  and #{search_price_end}>= newprice
  and title like CONCAT('%', #{search_input}, '%')

  and 
 
       probrand=  ${item} 



  and 
 
       style like CONCAT('%', #{item}, '%')



  and 
 
       state =  ${item}

select * from motomall_product 

parenttype=#{search_parenttype}
  and type=#{search_type}
  and `level`=#{search_level}
  and newprice/100 >= #{search_price_start}
  and #{search_price_end}>= newprice/100
  and title like CONCAT('%', #{search_input}, '%')

  and 
 
       probrand=  ${item} 



  and 
 
       style like CONCAT('%', #{item}, '%')



  and 
 
       state =  ${item} 




  order by ptime    
		
		   ASC
		
		
		    DESC
		
  limit #{start},#{limit}

insert.if:
