title=#{title}, des=#{des}, ptime=#{ptime}, picurl=#{picurl}, probrand=#{probrand}, parenttype=#{parenttype}, type=#{type}, style=#{style}, label=#{label}, brand=#{brand}, model=#{model}, keyword=#{keyword}, mallurl=#{mallurl}, `level`=#{level}, source=#{source}, ispackage=#{ispackage}, isrecommend=#{isrecommend}, oldprice=#{oldprice}, newprice=#{newprice}, state=#{state}, brandids=#{brandids}, modelids=#{modelids}, count=#{count}
id=#{id}

